import create from 'zustand';

const useCommentStore = create((set) => ({
  comments: [],
  addComment: (comment) => set((state) => ({ comments: [...state.comments, comment] })),
  deleteComment: (id) => set((state) => ({ comments: state.comments.filter(c => c.id !== id) })),
  editComment: (id, text) => set((state) => ({
    comments: state.comments.map(c => c.id === id ? { ...c, text } : c)
  })),
  loadComments: () => set((state) => {
    const savedComments = JSON.parse(localStorage.getItem('comments')) || [];
    return { comments: savedComments };
  }),
  saveComments: () => set((state) => {
    localStorage.setItem('comments', JSON.stringify(state.comments));
  }),
}));

export default useCommentStore;
