import { createStore } from 'redux';

const initialState = {
  comments: JSON.parse(localStorage.getItem('comments')) || [],
};

const commentReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'ADD_COMMENT':
      const updatedComments = [...state.comments, action.payload];
      localStorage.setItem('comments', JSON.stringify(updatedComments));
      return { ...state, comments: updatedComments };

    case 'DELETE_COMMENT':
      const filteredComments = state.comments.filter(c => c.id !== action.payload);
      localStorage.setItem('comments', JSON.stringify(filteredComments));
      return { ...state, comments: filteredComments };

    case 'EDIT_COMMENT':
      const editedComments = state.comments.map(c => c.id === action.payload.id ? { ...c, text: action.payload.text } : c);
      localStorage.setItem('comments', JSON.stringify(editedComments));
      return { ...state, comments: editedComments };

    case 'ADD_REPLY':
      const commentsWithReply = state.comments.map(c =>
        c.id === action.payload.commentId
          ? { ...c, replies: [...(c.replies || []), action.payload.reply] }
          : c
      );
      localStorage.setItem('comments', JSON.stringify(commentsWithReply));
      return { ...state, comments: commentsWithReply };

    case 'DELETE_REPLY':
      const commentsWithoutReply = state.comments.map(c =>
        c.id === action.payload.commentId
          ? { ...c, replies: (c.replies || []).filter(r => r.id !== action.payload.replyId) }
          : c
      );
      localStorage.setItem('comments', JSON.stringify(commentsWithoutReply));
      return { ...state, comments: commentsWithoutReply };

    default:
      return state;
  }
};

const store = createStore(commentReducer);

export default store;
