import React, { useEffect } from 'react';
import CommentList from './components/CommentItem/CommentList';
import AddCommentForm from './components/CommentForm/CommentForm';
import useCommentStore from './hooks/commentStore';
import './App.css';
import './components'

const App = () => {
  const comments = useCommentStore((state) => state.comments);
  const deleteComment = useCommentStore((state) => state.deleteComment);
  const editComment = useCommentStore((state) => state.editComment);
  const deleteReply = useCommentStore((state) => state.deleteReply);
  const loadComments = useCommentStore((state) => state.loadComments);

  useEffect(() => {
    loadComments();
  }, [loadComments]);

  return (
    <div className="App">
      <h1>Comments Section</h1>
      <AddCommentForm />
      {comments && comments.length > 0 ? (
        <CommentList
          comments={comments}
          onDelete={deleteComment}
          onEdit={editComment}
          onDeleteReply={deleteReply} // Pass the deleteReply function
        />
      ) : (
        <p>No comments yet. Be the first to add one!</p>
      )}
    </div>
  );
};

export default App;
