import React, { useEffect } from 'react';
import CommentCollection from './components/CommentItem/CommentCollection';
import CommentForm from './components/CommentForm/CommentForm';
import useCommentStore from './hooks/commentStore';
import './App.css';
import './components/styles/global.css'

const App = () => {
  const storedComments = useCommentStore((state) => state.storedComments);
  const removeComment = useCommentStore((state) => state.removeComment);
  const updateComment = useCommentStore((state) => state.updateComment);
  const removeReply = useCommentStore((state) => state.removeReply);
  const fetchComments = useCommentStore((state) => state.fetchComments);

  useEffect(() => {
    fetchComments();
  }, [fetchComments]);

  return (
    <div className="App">
      <h1>Comments Section</h1>
      <CommentForm />
      {storedComments && storedComments.length > 0 ? (
        <CommentCollection
          storedComments={storedComments}
          onRemove={removeComment}
          onUpdate={updateComment}
          onRemoveReply={removeReply}
        />
      ) : (
        <p>No comments yet. Be the first to add one!</p>
      )}
    </div>
  );
};

export default App;
