// src/components/CommentSection/CommentSection.js
import React from 'react';
import CommentList from '../CommentLis/CommentList';
import CommentForm from '../CommentForm/CommentForm';
import useCommentStore from '../../hooks/commentStore';
import './CommentSection.css';

const CommentSection = () => {
  const comments = useCommentStore((state) => state.comments);
  const deleteComment = useCommentStore((state) => state.deleteComment);
  const editComment = useCommentStore((state) => state.editComment);
  const deleteReply = useCommentStore((state) => state.deleteReply);

  return (
    <div className="comment-section">
      <CommentForm />
      {comments && comments.length > 0 ? (
        <CommentList
          comments={comments}
          onDelete={deleteComment}
          onEdit={editComment}
          onDeleteReply={deleteReply}
        />
      ) : (
        <p>No comments yet. Be the first to add one!</p>
      )}
    </div>
  );
};

export default CommentSection;
