import React, { useEffect } from 'react';
import CommentList from './components/CommentItem/CommentList';
import AddCommentForm from './components/CommentForm/CommentForm';
import { useSelector, useDispatch } from 'react-redux';
import { loadComments } from './hooks/s'; // Import the action creator for loading comments
import './App.css';
import './components/styles/global.css';

const App = () => {
  const comments = useSelector((state) => state.comments);
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(loadComments()); // Load comments from localStorage or other sources
  }, [dispatch]);

  const handleDeleteComment = (commentId) => {
    dispatch({ type: 'DELETE_COMMENT', payload: commentId });
  };

  const handleEditComment = (commentId, newText) => {
    dispatch({ type: 'EDIT_COMMENT', payload: { id: commentId, text: newText } });
  };

  const handleDeleteReply = (commentId, replyId) => {
    dispatch({ type: 'DELETE_REPLY', payload: { commentId, replyId } });
  };

  return (
    <div className="App">
      <h1>Comments Section</h1>
      <AddCommentForm />
      {comments && comments.length > 0 ? (
        <CommentList
          comments={comments}
          onDelete={handleDeleteComment}
          onEdit={handleEditComment}
          onDeleteReply={handleDeleteReply} 
        />
      ) : (
        <p>No comments yet. Be the first to add one!</p>
      )}
    </div>
  );
};

export default App;
