import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import AddCommentForm from '../CommentForm/CommentForm';
import './Comment.css';

const Comment = ({ comment }) => {
  const [isEditing, setIsEditing] = useState(false);
  const [editedText, setEditedText] = useState(comment.text);
  const [showReplies, setShowReplies] = useState(false);
  const dispatch = useDispatch();

  const handleEdit = () => {
    setIsEditing(true);
  };

  const handleSave = () => {
    dispatch({ type: 'EDIT_COMMENT', payload: { id: comment.id, text: editedText } });
    setIsEditing(false);
  };

  const toggleReplies = () => {
    setShowReplies(!showReplies);
  };

  return (
    <div className="comment">
      <h4>{comment.name} on {new Date(comment.date).toLocaleString()}</h4>
      {isEditing ? (
        <div>
          <textarea value={editedText} onChange={(e) => setEditedText(e.target.value)} />
          <button onClick={handleSave}>Save</button>
        </div>
      ) : (
        <p>{comment.text}</p>
      )}
      <button onClick={() => dispatch({ type: 'DELETE_COMMENT', payload: comment.id })}>Delete</button>
      {!isEditing && <button onClick={handleEdit}>Edit</button>}
      <button onClick={toggleReplies}>
        {showReplies ? 'Hide Replies' : 'Show Replies'}
      </button>
      {showReplies && (
        <div>
          {comment.replies && comment.replies.map((reply) => (
            <div key={reply.id} className="reply">
              <h5>{reply.name} on {new Date(reply.date).toLocaleString()}</h5>
              <p>{reply.text}</p>
              <button onClick={() => dispatch({ type: 'DELETE_REPLY', payload: { commentId: comment.id, replyId: reply.id } })}>Delete Reply</button>
            </div>
          ))}
          <AddCommentForm parentId={comment.id} />
        </div>
      )}
    </div>
  );
};

export default Comment;
